    [TestClass]
    public class AgnosticStringReplaceTests
    {
        [TestMethod]
        public void TestMethod1()
        {
            //Arrange
            var s = "Hello my name is bob";
            var expected = "Hello your name is bob";
            var source = "my";
            var target = "your";

            //Act
            var solver = new AgnosticStringReplace.Solution();
            var res = solver.Replace(s, source, target);

            //Assert
            Assert.AreEqual(expected, res);
        }

        [TestMethod]
        public void TestMethod2()
        {
            //Arrange
            var s = "HELLO MY NAME IS BOB";
            var expected = "HELLO YOUR NAME IS BOB";
            var source = "my";
            var target = "YOUR";

            //Act
            var solver = new AgnosticStringReplace.Solution();
            var res = solver.Replace(s, source, target);

            //Assert
            Assert.AreEqual(expected, res);
        }

        [TestMethod]
        public void TestMethod3()
        {
            //Arrange
            var s = "AaAaAa";
            var expected = "bbbbbb";
            var source = "a";
            var target = "b";

            //Act
            var solver = new AgnosticStringReplace.Solution();
            var res = solver.Replace(s, source, target);

            //Assert
            Assert.AreEqual(expected, res);
        }

        [TestMethod]
